if (s > threshold) { \
        if (s > max_sample) { \
            max_sample = s; \
            threshold = min(1.0, s); \
        } \
        if (s > 1.0) { \
            s = fmod(s + 1.0, 2.0) - 1.0; \
            (x) = s; \
        } \
    } else if (s < -threshold) { \
        if (s < -max_sample) { \
            max_sample = -s; \
            threshold = min(1.0, -s); \
        } \
        if (s < -1.0) { \
            s = -(fmod(-s + 1.0, 2.0) - 1.0); \
            (x) = s; \
        } \
    }

if (s > max_sample) { \
            max_sample = s; \
        } else if (s < -max_sample) { \
            max_sample = -s; \
        }
